data.json_ui_schema:
{
    "ui:classNames": "row",
    "instance_name": {
        "ui:classNames": "col-6",
        "ui:help": "Nombre de la instancia en la configuración."
    },
    "indexName": {
        "ui:classNames": "col-6",
        "ui:help": "Indice sobre el que realizar la acción."
    },
    "postProcessHitsToString": {
