    },
    "query_params": {
        "ui:classNames": "row",
        "operation": {
            "ui:classNames": "col-6",
            "ui:help": "Elasticsearch camel operation. https://camel.apache.org/components/3.14.x/elasticsearch-rest-component.html#_message_operations"
        }
    }
