data.json_data_schema:
{
    "description": "File Output",
    "type": "object",
    "required": [
        "label",
        "directory"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "directory": {
            "title": "Directorio",
            "type": "string"
        },
        "fileName": {
            "type": "string",
            "title": "Nombre del archivo en destino "
        },
        "query_params": {
            "title": "Opciones",
            "type": "object",
            "properties": {
                "doneFileName": {
                    "type": "string",
                    "title": "Crear un fichero adiciónal para indicar su estado "
                },
                "bufferSize": {
                    "type": "number",
                    "default": 131072,
                    "title": "Tamaño de escritura del buffer (Bytes)"
                },
                "fileExist": {
                    "type": "string",
                    "title": " ¿ Que hacer si el archivo existe ?",
                    "enum": [
                        "Override",
                        "Append",
                        "Fail"
                    ]
                },
                "allowNullBody": {
                    "type": "string",
                    "default": "false",
                    "title": "Permitir contenido vacio"
                },
                "flatten": {
                    "type": "string",
                    "title": "Ignorar estructura directorios original"
                },
                "items": {
                    "title": "Parámetros extra",
                    "type": "array",
                    "items": {
                        "type": "object",
                        "required": [
                            "code",
                            "value"
                        ],
                        "properties": {
                            "code": {
                                "title": "Clave",
                                "type": "string"
                            },
                            "value": {
                                "title": "Valor",
                                "type": "string"
                            }
                        }
                    }
                }
            }
        }
    }
}

data.json_ui_schema:
{
    "ui:classNames": "row",
    "label": {
        "ui:classNames": "col-4"
    },
    "directory": {
        "ui:classNames": "col-8"
    },
    "fileName": {
        "ui:classNames": "col-4",
        "ui:help": "Si no se introduce sera un ID autogenerado"
    },
    "query_params": {
        "ui:classNames": "row",
        "doneFileName": {
            "ui:classNames": "col-4",
            "ui:help": "done-${file:name} ||| ${file:name}.done "
        },
        "bufferSize": {
            "ui:classNames": "col-4"
        },
        "fileExist": {
            "ui:classNames": "col-4",
            "ui:enumNames": [
                "Sobreescribir",
                "Adjuntar",
                "Marcar como error"
            ]
        },
        "allowNullBody": {
            "ui:widget": "ListSwitchField",
            "ui:help": "Permite que el contenido recibido sea null.",
            "ui:classNames": "col-4"
        },
        "flatten": {
            "ui:widget": "ListSwitchField",
            "ui:classNames": "col-4",
            "ui:help": "Por defecto se utiliza la estructura de directorios leída (si existe) para recrearla en el destino. Si se activa se ignorará dicha estructura."
        },
        "items": {
            "ui:classNames": "full-width"
        }
    }
}
