data.json_data_schema:
{
    "description": "FTP Client",
    "type": "object",
    "required": [
        "hostname",
        "port"
    ],
    "properties": {
        "hostname": {
            "title": "Host",
            "type": "string"
        },
        "protocol": {
            "type": "string",
            "default": "ftp",
            "title": "Protocolo transporte",
            "enum": [
                "ftp",
                "sftp",
                "ftps"
            ],
            "enumNames": [
                "FTP",
                "SFTP",
                "FTPS"
            ]
        },
        "port": {
            "title": "Puerto",
            "type": "string"
        },
        "username": {
            "title": "Nombre de usuario",
            "type": "string"
        },
        "password": {
            "title": "Contraseña",
            "type": "string"
        },
        "filename": {
            "title": "Archivo",
            "type": "string"
        },
        "directoryname": {
            "title": "Carpeta Destino",
            "type": "string"
        },
        "query_params": {
            "title": "Opciones",
            "type": "object",
            "properties": {

                "sslContextParameters": {
                    "type": "string",
                    "title": "Certificado"
                }
            }
        },
        "extra_query_params": {
            "title": "Parámetros extra",
            "type": "array",
            "items": {
                "type": "object",
                "required": [
                    "code",
                    "value"
                ],
                "properties": {
                    "code": {
                        "title": "Clave",
                        "type": "string"
                    },
                    "value": {
                        "title": "Valor",
                        "type": "string"
                    }
                }
            }
        }
    }
}
