data.json_data_schema:
{
    type: 'object',
    required: [
        'label',
        'code',
    ],
    properties: {
        label: {
            title: "Etiqueta",
            type: 'string',
        },
        code: {
            title: 'Cabeceras',
            type: 'array',
            items: {
                type: 'object',
                required: [
                    'code',
                    'value',
                    'format',
                ],
                properties: {
                    code: {
                        title: 'Clave',
                        type: 'string',
                    },

                    value: {
                        title: 'Valor',
                        type: 'string',
                    },
                    format: {
                        "title": "Formato",
                        type: 'string',
                        default: 'constant',
                        enum: [
                            'constant',
                            'simple',
                            'groovy',
                            'header',
                        ],
                    },
                },
            },
        },
    },
}
