data.json_data_schema:
{
    title: "Inyectar Mensaje",
    description: "Permite la inyección de un mensaje personalizado en un nodo",
    type: "object",
    required: [
        "label",
    ],
    properties: {
        label: {
            title: "Etiqueta",
            type: "string",
        },
        data: {
            title: "Configuración",
            properties: {
                code: {
                    title: "Body",
                    type: "string",
                },
                headers: {
                    title: "Cabeceras",
                    type: "array",
                    items: {
                        type: "object",
                        required: [
                            "code",
                            "value",
                            "format",
                        ],
                        properties: {
                            code: {
                                title: "Clave",
                                type: "string",
                            },

                            value: {
                                title: "Valor",
                                type: "string",
                            },
                            format: {
                                "title": "Formato",
                                type: "string",
                                default: "constant",
                                enum: [
                                    "constant",
                                    "simple",
                                    "groovy",
                                    "header",
                                ],
                            },
                        },
                    },
                },
                properties: {
                    title: "Propiedades",
                    type: "array",
                    items: {
                        type: "object",
                        required: [
                            "code",
                            "value",
                            "format",
                        ],
                        properties: {
                            code: {
                                title: "Clave",
                                type: "string",
                            },
                            value: {
                                title: "Valor",
                                type: "string",
                            },
                            format: {
                                "title": "Formato",
                                type: "string",
                                default: "constant",
                                enum: [
                                    "constant",
                                    "simple",
                                    "groovy",
                                    "header",
                                ],
                            },
                        },
                    },
                },
            },
        },
    },
}
