data.json_data_schema:
{
    "description": "Mail Sender",
    "type": "object",
    "required": [
        "label",
        "hostname",
        "port",
        "type",
        "contentType"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "type": {
            "title": "Tipo",
            "type": "string",
            "enum": [
                "smtp",
                "imap",
                "smtps"
            ]
        },
        "hostname": {
            "title": "Host",
            "type": "string"
        },
        "port": {
            "title": "Puerto",
            "type": "string"
        },
        "contentType": {
            "title": "Tipo del mensaje",
            "type": "string",
            "enum": [
                "auto",
                "text/html",
                "text/plain"
            ]
        },
        "query_params": {
            "title": "Opciones",
            "type": "object",
            "required": [
                "username",
                "password"
            ],
            "properties": {
                "username": {
                    "title": "Usuario",
                    "type": "string"
                },
                "password": {
                    "title": "Contrase√±a",
                    "type": "string"
                },
                "from": {
                    "title": "Remitente",
                    "type": "string"
                },
                "to": {
                    "title": "Destinatario",
                    "type": "array",
                    "items": {
                        "type": "string"
                    }
                },
                "cc": {
                    "title": "Con copia",
                    "type": "array",
                    "items": {
                        "type": "string"
                    }
                },
                "bcc": {
                    "title": "Copia oculta",
                    "type": "array",
                    "items": {
                        "type": "string"
                    }
                },
                "subject": {
                    "title": "Asunto",
                    "type": "string"
                }
            }
        },
        "message_html": {
            "title": "Mensaje",
            "type": "string",
            "default": ""
        },
        "message_text": {
            "title": "Mensaje",
            "type": "string",
            "default": ""
        },
        "attachments": {
            "title": "Adjuntos",
            "type": "array",
            "required": [
                "attachment",
                "attachment_type",
                "filename"
            ],
            "items": {
                "type": "object",
                "properties": {
                    "attachment": {
                        "title": "Adjunto",
                        "type": "string"
                    },
                    "attachment_type": {
                        "title": "Tipo",
                        "type": "string"
                    },
                    "filename": {
                        "title": "Nombre fichero",
                        "type": "string"
                    }
                }
            }
        }
    }
}

data.json_ui_schema:
{
    "ui:classNames": "row",
    "label": {
        "ui:classNames": "col-3"
    },
    "type": {
        "ui:classNames": "col-2",
        "ui:enumNames": [
            "smtp",
            "imap",
            "smtps"
        ]
    },
    "hostname": {
        "ui:classNames": "col-5"
    },
    "port": {
        "ui:classNames": "col-2"
    },
    "contentType": {
        "ui:classNames": "col-3",
            "ui:enumNames": [
                "Auto",
                "HTML",
                "Texto"
            ]
    },
    "query_params": {
        "ui:classNames": "row",
        "username": {
            "ui:classNames": "col-4"
        },
        "password": {
            "ui:classNames": "col-4"
        },
        "from": {
            "ui:classNames": "col-4",
            "ui:options": {
                "inputType": "email"
            }
        },
        "to": {
            "ui:classNames": "full-width"
        },
        "cc": {
            "ui:classNames": "full-width"
        },
        "bcc": {
            "ui:classNames": "full-width"
        },
        "subject": {
            "ui:classNames": "col-9"
        }
    },
    "message_html": {
        "condition": "contentType=text/html",
        "ui:classNames": "col-12",
        "ui:widget": "AceEditorWidget",
        "ui:mode": "html",
        "ui:disableValidation": true,
        "ui:beautify": true,
        "ui:height": 200
    },
    "message_text": {
        "condition": "contentType=text/plain",
        "ui:classNames": "col-12",
        "ui:widget": "AceEditorWidget",
        "ui:mode": "text",
        "ui:disableValidation": true,
        "ui:beautify": true,
        "ui:height": 200
    },
    "attachments": {
        "items": {
            "attachment": {
                "ui:classNames": "col-12",
                "ui:widget": "AceEditorWidget",
                "ui:disableValidation": true,
                "ui:beautify": true,
                "ui:height": 200
            }
        }
    }
}
