data.json_data_schema:
{
    "description": "OIDC Tokens Manager",
    "type": "object",
    "required": [
        "label",
        "oidcClientId",
        "oidcClientSecret",
        "oidcClientURL",
        "tenantAlias"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "oidcClientId": {
            "title": "OIDC Client ID",
            "type": "string"
        },
        "oidcClientSecret": {
            "title": "OIDC Client Secret",
            "type": "string"
        },
        "oidcClientURL": {
            "title": "OIDC Client URL",
            "type": "string"
        },
        "tenantAlias": {
            "title": "Tenant Alias",
            "type": "string"
        },
        "tokenHeaderOutput": {
            "title": "Token Header Output",
            "type": "string"
        },
        "withBearer": {
            "title": "Include Bearer prefix",
            "type": "boolean"
        }
    }
}

data.json_ui_schema:
{
    "ui:classNames": "row",
    "label": {
        "ui:classNames": "col-12"
    },
    "oidcClientId": {
        "ui:classNames": "col-3"
    },
    "oidcClientSecret": {
        "ui:classNames": "col-3",
        "ui:widget": "password"
    },
    "oidcClientURL": {
        "ui:classNames": "col-3"
    },
    "tenantAlias": {
        "ui:classNames": "col-3"
    },
    "tokenHeaderOutput": {
        "ui:classNames": "col-3",
        "ui:help": "En que cabecera se almacenará el token. Por defecto, Authorization."
    },
    "withBearer": {
        "ui:widget": "Switch",
        "ui:classNames": "col-3",
        "ui:help": "Determina si se incluirá el prefijo Bearer en la cabecera."
    }
}
