data.json_data_schema:
{
    "title": " ",
    "description": "Split message body",
    "type": "object",
    "required": [
        "label"
    ],
    "properties": {
        "label": {
            "type": "string",
            "title": "Etiqueta"
        },
        "body": {
            "type": "string",
            "title": "Cuerpo (simple)"
        },
        "delimiter": {
            "type": "string",
            "title": "Separador"
        },
        "streaming": {
            "type": "boolean",
            "default": false,
            "title": "Streaming mode"
        },
        "tokenize": {
            "type": "boolean",
            "default": false,
            "title": "Tokernizer mode"
        },
        "parallel": {
            "type": "boolean",
            "default": false,
            "title": "Parallel mode"
        },
        "tokenize_options": {
            "type": "object",
            "properties": {
                "token": {
                    "type": "string",
                    "title": "Token"
                },
                "regex": {
                    "type": "boolean",
                    "default": false,
                    "title": "Regex"
                },
                "xml": {
                    "type": "boolean",
                    "default": false,
                    "title": "XML"
                },
                "trim": {
                    "type": "boolean",
                    "default": true,
                    "title": "Trim"
                },
                "group": {
                    "type": "string",
                    "title": "Group Size"
                }
            }
        }
    }
}

data.json_ui_schema:
{
    "ui:classNames": "row",
    "label": {
        "ui:classNames": "col-12"
    },
    "body": {
        "ui:classNames": "col-12",
        "ui:help": "Establece en código simple que elemento del body se procesará."
    },
    "streaming": {
        "ui:widget": "checkbox",
        "ui:classNames": "col-4",
        "ui:help": "Separa los mensajes de forma directa consumiendo menos memoria."
    },
    "delimiter": {
        "ui:classNames": "col-4",
        "ui:help": "Caracter separador para los body de tipo String. Default: (,)"
    },
    "tokenize": {
        "ui:widget": "checkbox",
        "ui:classNames": "col-4",
        "ui:help": "Habilitar modo tokenizer ignorando la propiedad delimiter."
    },
    "parallel": {
        "ui:widget": "checkbox",
        "ui:classNames": "col-4",
        "ui:help": "Procesa los mensajes separados en paralelo. "
    },
    "tokenize_options": {
        "ui:classNames": "row",
        "condition": "tokenize=true",
        "token": {
            "ui:classNames": "col-4",
            "ui:help": "Token a utilizar para el procesado"
        },
        "regex": {
            "ui:widget": "checkbox",
            "ui:classNames": "col-4",
            "ui:help": "El token se procesará como una expr. regular"
        },
        "xml": {
            "ui:widget": "checkbox",
            "ui:classNames": "col-4",
            "ui:help": "El token se procesará como una expr. xpath. Solo body XML"
        },
        "trim": {
            "ui:widget": "checkbox",
            "ui:classNames": "col-4",
            "ui:help": "Eliminar espacios en blanco y saltos al inicio y final del mensaje resultante"
        },
        "group": {
            "ui:classNames": "col-4",
            "ui:help": "Número de elementos para agrupar. Util para agrupar lineas (\\n) por bloques, etc."
        }
    }
}

data.defaults:
{
    "handles": [
        {
            "id": "out0",
            "to": "",
            "title": "Loop",
            "color": "blue"
        },
        {
            "id": "out1",
            "to": "",
            "title": "Final",
            "color": "red"
        }
    ],
    "body": "${body}",
    "streaming": true,
    "delimiter": ",",
    "tokenize": false,
    "parallel": false,
    "tokenize_options": {
        "token": "",
        "regex": false,
        "xml": false,
        "trim": true,
        "group": ""
    },
    "input_format": "raw",
    "output_format": "raw"
}
