data.json_data_schema:
{
    "description": "RabbitMQ Input",
    "type": "object",
    "required": [
        "label",
        "instance_name",
        "queue"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "instance_name": {
            "title": "Instance name",
            "type": "string"
        },
        "queue": {
            "title": "Nombre de la cola",
            "type": "string"
        },
        "query_params": {
            "title": "Parámetros",
            "type": "object",
            "properties": {
                "acknowledgeMode": {
                    "title": "Acknowledge Mode",
                    "type": "string",
                    "default": "NONE",
                    "enum": [
                        "NONE",
                        "MANUAL",
                        "AUTO"
                    ],
                    "enumNames": [
                        "NONE",
                        "MANUAL",
                        "AUTO"
                    ]
                },
                "arg.queue.autoDelete": {
                    "title": "Auto delete Queue",
                    "type": "boolean",
                    "default": false
                },
                "arg.queue.durable": {
                    "title": "Durable",
                    "type": "boolean",
                    "default": true
                },
                "autoDeclare": {
                    "title": "AutoDeclare",
                    "type": "boolean",
                    "default": true
                },
                "prefetchCount": {
                    "title": "prefetchCount",
                    "type": "number",
                    "default": 10
                }
            }
        },
        "extra_query_params": {
            "title": "Parámetros Extra",
            "type": "array",
            "items": {
                "type": "object",
                "required": [
                    "code",
                    "value"
                ],
                "properties": {
                    "code": {
                        "title": "Clave",
                        "type": "string"
                    },
                    "value": {
                        "title": "Valor",
                        "type": "string"
                    }
                }
            }
        }
    }
}
