data.json_data_schema:
{
    "description": "RabbitMQ Output",
    "type": "object",
    "required": [
        "label",
        "instance_name"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "instance_name": {
            "title": "Instance name",
            "type": "string"
        },
        "queue": {
            "title": "Nombre cola RabbitMQ",
            "type": "string"
        },
        "exchange": {
            "title": "Exchange",
            "type": "string"
        },
        "query_params": {
            "title": "Parámetros",
            "type": "object",
            "properties": {
                "confirmTimeout": {
                    "title": "Specify the timeout in milliseconds to be used when waiting for a message sent to be confirmed",
                    "type": "number",
                    "default": 1000
                },
                "confirm": {
                    "title": "Throw exception if  message cannot be delivered",
                    "type": "string",
                    "default": "enabled",
                    "enum": [
                        "auto",
                        "enabled",
                        "disabled"
                    ],
                    "enumNames": [
                        "AUTO",
                        "ENABLED",
                        "DISABLED"
                    ]
                },
                "arg.queue.autoDelete": {
                    "title": "Elimina el exchange si no se esta usando (No recomendado)",
                    "type": "boolean",
                    "default": true
                }
            }
        },
        "extra_query_params": {
            "title": "Parámetros Extra",
            "type": "array",
            "items": {
                "type": "object",
                "required": [
                    "code",
                    "value"
                ],
                "properties": {
                    "code": {
                        "title": "Clave",
                        "type": "string"
                    },
                    "value": {
                        "title": "Valor",
                        "type": "string"
                    }
                }
            }
        }
    }
}
