data.json_ui_schema:
{
    "ui:classNames": "row",
    "label": {
        "ui:classNames": "col-12"  
    },
    "timePeriodMillis": {
        "ui:classNames": "col-4",
        "ui:help": "Sets the time period during which the maximum request count is valid for."
    },
    "numberOfRequests": {
        "ui:classNames": "col-4",
        "ui:help": "The maximum number of requests for each period of time defined."
    },
    "rejectExecution": {
        "ui:widget": "checkbox",
        "ui:classNames": "col-4",
        "ui:help": "If true, messages are discarded when the exchange exceeds the request limit."
    }
}
